// frontend/src/pages/SchedulePage.tsx

import React, { useState, useEffect } from 'react';
import Calendar from '../components/schedule/Calendar';
import ScheduleForm from '../components/schedule/ScheduleForm';
import MonthlySchedule from '../components/schedule/MonthlySchedule';
import { Schedule } from '../types';
import { scheduleAPI, apiUtils } from '../services/api';

const SchedulePage: React.FC = () => {
  const [schedules, setSchedules] = useState<Schedule[]>([]);
  const [selectedDate, setSelectedDate] = useState<string>(
    new Date().toISOString().split('T')[0]
  );
  const [currentDate, setCurrentDate] = useState<Date>(new Date());
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  // Ïä§ÏºÄÏ§Ñ Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  const loadSchedules = async () => {
    try {
      setLoading(true);
      const year = currentDate.getFullYear();
      const month = currentDate.getMonth() + 1;
      
      const response = await scheduleAPI.getSchedules({
        year: year,
        month: month
      });
      
      setSchedules(response.results);
      setError('');
    } catch (err) {
      setError(apiUtils.getErrorMessage(err));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadSchedules();
  }, [currentDate]);

  // Ïä§ÏºÄÏ§Ñ Ï∂îÍ∞Ä ÏÑ±Í≥µ Ïãú Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
  const handleScheduleAdded = () => {
    loadSchedules();
  };

  // Ïõî Î≥ÄÍ≤Ω
  const changeMonth = (direction: number) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(newDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  // ÎÇ†Ïßú ÏÑ†ÌÉù
  const handleDateSelect = (date: string) => {
    setSelectedDate(date);
  };

  if (loading) {
    return (
      <div>
        <div className="ig-card">
          <div className="ig-card-header">
            <h2 className="ig-card-title">üìÖ Ïä§ÏºÄÏ§Ñ</h2>
          </div>
          <div className="ig-card-content">
            <div className="loading">Ïä§ÏºÄÏ§ÑÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div>
      {/* Ïä§ÌÜ†Î¶¨ Ïä§ÌÉÄÏùº ÌÄµ Ïï°ÏÑ∏Ïä§ */}
      <div className="ig-card mb-24">
        <div className="ig-card-content">
          <div className="story-container">
            <div className="story-item">
              <div className="story-avatar">
                <div className="story-avatar-inner">üìÖ</div>
              </div>
              <div className="story-username">Ïò§Îäò ÏùºÏ†ï</div>
            </div>
            <div className="story-item">
              <div className="story-avatar">
                <div className="story-avatar-inner">‚ûï</div>
              </div>
              <div className="story-username">ÏùºÏ†ï Ï∂îÍ∞Ä</div>
            </div>
            <div className="story-item">
              <div className="story-avatar">
                <div className="story-avatar-inner">üìä</div>
              </div>
              <div className="story-username">ÌÜµÍ≥Ñ</div>
            </div>
            <div className="story-item">
              <div className="story-avatar">
                <div className="story-avatar-inner">üèãÔ∏è</div>
              </div>
              <div className="story-username">PT</div>
            </div>
          </div>
        </div>
      </div>

      {error && (
        <div className="error mb-24">
          {error}
        </div>
      )}

      <div className="grid grid-2 gap-24">
        <div>
          <div className="ig-card">
            <div className="ig-card-header">
              <h3 className="ig-card-title">Îã¨Î†•</h3>
              <div className="text-sm text-muted">
                {schedules.length}Í∞úÏùò ÏùºÏ†ï
              </div>
            </div>
            <div className="ig-card-content" style={{ padding: 0 }}>
              <Calendar
                currentDate={currentDate}
                schedules={schedules}
                selectedDate={selectedDate}
                onDateSelect={handleDateSelect}
                onMonthChange={changeMonth}
              />
            </div>
          </div>
        </div>

        <div>
          <div className="ig-card">
            <div className="ig-card-header">
              <h3 className="ig-card-title">ÏÉà ÏùºÏ†ï</h3>
              <div className="text-sm text-muted">
                {new Date(selectedDate).toLocaleDateString('ko-KR', {
                  month: 'long',
                  day: 'numeric'
                })}
              </div>
            </div>
            <div className="ig-card-content">
              <ScheduleForm
                selectedDate={selectedDate}
                onScheduleAdded={handleScheduleAdded}
              />
            </div>
          </div>
        </div>
      </div>

      <MonthlySchedule
        schedules={schedules}
        currentDate={currentDate}
      />
    </div>
  );
};

export default SchedulePage;
