// frontend/src/components/members/MemberList.tsx

import React, { useState, useEffect } from 'react';
import { Member, User, CreateMemberData } from '../../types';
import { memberAPI, userAPI, apiUtils } from '../../services/api';
import { getTodayString } from '../../utils/dateUtils';
import './MemberList.css';

interface MemberListProps {
  members: Member[];
  selectedMember: Member | null;
  onMemberSelect: (member: Member) => void;
  onMemberAdded: () => void;
}

const MemberList: React.FC<MemberListProps> = ({
  members,
  selectedMember,
  onMemberSelect,
  onMemberAdded
}) => {
  const [showAddForm, setShowAddForm] = useState<boolean>(false);
  const [formData, setFormData] = useState<CreateMemberData>({
    name: '',
    phone: '',
    email: '',
    gender: undefined,
    birth_date: '',
    join_date: getTodayString(),
    trainer: undefined,
    goals: '',
    health_notes: '',
    emergency_contact: ''
  });
  const [trainers, setTrainers] = useState<User[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  // Ìä∏Î†àÏù¥ÎÑà Î™©Î°ù Î°úÎìú
  useEffect(() => {
    const loadTrainers = async () => {
      try {
        const response = await userAPI.getTrainers();
        setTrainers(response.results);
      } catch (err) {
        console.error('Ìä∏Î†àÏù¥ÎÑà Î°úÎìú Ïã§Ìå®:', err);
      }
    };

    loadTrainers();
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'trainer' ? (value ? parseInt(value) : undefined) : value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.name || !formData.phone || !formData.join_date) {
      setError('Ïù¥Î¶Ñ, Ï†ÑÌôîÎ≤àÌò∏, Í∞ÄÏûÖÏùºÏùÄ ÌïÑÏàò ÏûÖÎ†•ÏÇ¨Ìï≠ÏûÖÎãàÎã§.');
      return;
    }

    try {
      setLoading(true);
      setError('');
      
      await memberAPI.createMember(formData);
      
      // Ìèº Ï¥àÍ∏∞Ìôî
      setFormData({
        name: '',
        phone: '',
        email: '',
        gender: undefined,
        birth_date: '',
        join_date: getTodayString(),
        trainer: undefined,
        goals: '',
        health_notes: '',
        emergency_contact: ''
      });
      
      setShowAddForm(false);
      onMemberAdded();
      
    } catch (err) {
      setError(apiUtils.getErrorMessage(err));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="member-list">
      {/* ÌöåÏõê Ï∂îÍ∞Ä Ìèº */}
      <div className="member-add-form">
        <div className="form-header">
          <h4>ÏÉà ÌöåÏõê Îì±Î°ù</h4>
          <button
            className="btn-secondary"
            onClick={() => setShowAddForm(!showAddForm)}
          >
            {showAddForm ? 'Ï∑®ÏÜå' : 'ÌöåÏõê Ï∂îÍ∞Ä'}
          </button>
        </div>

        {showAddForm && (
          <form onSubmit={handleSubmit} className="add-form">
            {error && <div className="error">{error}</div>}
            
            <div className="form-row">
              <div className="form-group">
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  placeholder="ÌöåÏõê Ïù¥Î¶Ñ*"
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="Ï†ÑÌôîÎ≤àÌò∏*"
                  required
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  placeholder="Ïù¥Î©îÏùº"
                />
              </div>
              <div className="form-group">
                <select
                  name="gender"
                  value={formData.gender || ''}
                  onChange={handleInputChange}
                >
                  <option value="">ÏÑ±Î≥Ñ ÏÑ†ÌÉù</option>
                  <option value="M">ÎÇ®ÏÑ±</option>
                  <option value="F">Ïó¨ÏÑ±</option>
                </select>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>ÏÉùÎÖÑÏõîÏùº</label>
                <input
                  type="date"
                  name="birth_date"
                  value={formData.birth_date}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Í∞ÄÏûÖÏùº</label>
                <input
                  type="date"
                  name="join_date"
                  value={formData.join_date}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>

            <div className="form-group">
              <select
                name="trainer"
                value={formData.trainer || ''}
                onChange={handleInputChange}
              >
                <option value="">Îã¥Îãπ Ìä∏Î†àÏù¥ÎÑà ÏÑ†ÌÉù</option>
                {trainers.map(trainer => (
                  <option key={trainer.id} value={trainer.id}>
                    {trainer.full_name}
                  </option>
                ))}
              </select>
            </div>

            <button 
              type="submit" 
              className="btn-primary" 
              disabled={loading}
              style={{ width: '100%' }}
            >
              {loading ? 'Îì±Î°ù Ï§ë...' : 'ÌöåÏõê Ï∂îÍ∞Ä'}
            </button>
          </form>
        )}
      </div>

      {/* ÌöåÏõê Î™©Î°ù */}
      <div className="member-list-header">
        <h4>ÌöåÏõê Î™©Î°ù ({members.length}Î™Ö)</h4>
      </div>

      <div className="member-list-container">
        {members.length === 0 ? (
          <p className="empty-message">Îì±Î°ùÎêú ÌöåÏõêÏù¥ ÏóÜÏäµÎãàÎã§.</p>
        ) : (
          members.map(member => (
            <div
              key={member.id}
              className={`member-item ${selectedMember?.id === member.id ? 'active' : ''}`}
              onClick={() => onMemberSelect(member)}
            >
              <div className="member-avatar">
                {member.name.charAt(0)}
              </div>
              <div className="member-info">
                <div className="member-name">{member.name}</div>
                <div className="member-details">
                  üìû {member.phone}
                  {member.trainer_name && ` | Îã¥Îãπ: ${member.trainer_name}`}
                </div>
                <div className="member-join-date">
                  Í∞ÄÏûÖÏùº: {new Date(member.join_date).toLocaleDateString('ko-KR')}
                </div>
              </div>
              {!member.is_active && (
                <div className="inactive-badge">ÎπÑÌôúÏÑ±</div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default MemberList;
