// frontend/src/pages/SchedulePage.tsx

import React, { useState, useEffect } from 'react';
import Calendar from '../components/schedule/Calendar';
import ScheduleForm from '../components/schedule/ScheduleForm';
import MonthlySchedule from '../components/schedule/MonthlySchedule';
import { Schedule } from '../types';
import { scheduleAPI, apiUtils } from '../services/api';

const SchedulePage: React.FC = () => {
  const [schedules, setSchedules] = useState<Schedule[]>([]);
  const [selectedDate, setSelectedDate] = useState<string>(
    new Date().toISOString().split('T')[0]
  );
  const [currentDate, setCurrentDate] = useState<Date>(new Date());
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  // ìŠ¤ì¼€ì¤„ ë°ì´í„° ë¡œë“œ
  const loadSchedules = async () => {
    try {
      setLoading(true);
      const year = currentDate.getFullYear();
      const month = currentDate.getMonth() + 1;
      
      const response = await scheduleAPI.getSchedules({
        year: year,
        month: month
      });
      
      setSchedules(response.results);
      setError('');
    } catch (err) {
      setError(apiUtils.getErrorMessage(err));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadSchedules();
  }, [currentDate]);

  // ìŠ¤ì¼€ì¤„ ì¶”ê°€ ì„±ê³µ ì‹œ ëª©ë¡ ìƒˆë¡œê³ ì¹¨
  const handleScheduleAdded = () => {
    loadSchedules();
  };

  // ì›” ë³€ê²½
  const changeMonth = (direction: number) => {
    const newDate = new Date(currentDate);
    newDate.setMonth(newDate.getMonth() + direction);
    setCurrentDate(newDate);
  };

  // ë‚ ì§œ ì„ íƒ
  const handleDateSelect = (date: string) => {
    setSelectedDate(date);
  };

  if (loading) {
    return (
      <div>
        <h2 style={{ marginBottom: '30px', color: '#2c3e50' }}>ðŸ“… ìŠ¤ì¼€ì¤„ ê´€ë¦¬</h2>
        <div className="loading">ë¡œë”© ì¤‘...</div>
      </div>
    );
  }

  return (
    <div>
      <h2 style={{ marginBottom: '30px', color: '#2c3e50' }}>ðŸ“… ìŠ¤ì¼€ì¤„ ê´€ë¦¬</h2>
      
      {error && (
        <div className="error">
          {error}
        </div>
      )}

      <div style={{ display: 'flex', gap: '30px', flexWrap: 'wrap' }}>
        <div style={{ flex: 2, minWidth: '400px' }}>
          <Calendar
            currentDate={currentDate}
            schedules={schedules}
            selectedDate={selectedDate}
            onDateSelect={handleDateSelect}
            onMonthChange={changeMonth}
          />
        </div>

        <div style={{ flex: 1, minWidth: '300px' }}>
          <ScheduleForm
            selectedDate={selectedDate}
            onScheduleAdded={handleScheduleAdded}
          />
        </div>
      </div>

      <MonthlySchedule
        schedules={schedules}
        currentDate={currentDate}
      />
    </div>
  );
};

export default SchedulePage;
