// frontend/src/pages/MembersPage.tsx

import React, { useState, useEffect } from 'react';
import MemberList from '../components/members/MemberList';
import MemberDetail from '../components/members/MemberDetail';
import { Member } from '../types';
import { memberAPI, apiUtils } from '../services/api';

const MembersPage: React.FC = () => {
  const [members, setMembers] = useState<Member[]>([]);
  const [selectedMember, setSelectedMember] = useState<Member | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string>('');

  // ÌöåÏõê Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  const loadMembers = async () => {
    try {
      setLoading(true);
      const response = await memberAPI.getMembers();
      setMembers(response.results);
      setError('');
    } catch (err) {
      setError(apiUtils.getErrorMessage(err));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadMembers();
  }, []);

  // ÌöåÏõê ÏÑ†ÌÉù
  const handleMemberSelect = (member: Member) => {
    setSelectedMember(member);
  };

  // ÌöåÏõê Ï∂îÍ∞Ä ÏÑ±Í≥µ Ïãú Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
  const handleMemberAdded = () => {
    loadMembers();
  };

  if (loading) {
    return (
      <div>
        <h2 style={{ marginBottom: '30px', color: '#2c3e50' }}>üë• ÌöåÏõê Í¥ÄÎ¶¨</h2>
        <div className="loading">Î°úÎî© Ï§ë...</div>
      </div>
    );
  }

  return (
    <div>
      <h2 style={{ marginBottom: '30px', color: '#2c3e50' }}>üë• ÌöåÏõê Í¥ÄÎ¶¨</h2>
      
      {error && (
        <div className="error">
          {error}
        </div>
      )}

      <div style={{ display: 'flex', gap: '30px', height: '700px' }}>
        <div style={{ flex: 1, background: '#f8f9fa', borderRadius: '10px', padding: '20px', overflowY: 'auto' }}>
          <MemberList
            members={members}
            selectedMember={selectedMember}
            onMemberSelect={handleMemberSelect}
            onMemberAdded={handleMemberAdded}
          />
        </div>

        <div style={{ flex: 2, background: '#f8f9fa', borderRadius: '10px', padding: '20px', display: 'flex', flexDirection: 'column' }}>
          <MemberDetail
            selectedMember={selectedMember}
          />
        </div>
      </div>
    </div>
  );
};

export default MembersPage;
